const CryptoJS=require("crypto-js");const key=CryptoJS.enc.Utf8.parse("mysecretkey123456");const iv=CryptoJS.enc.Utf8.parse("mysecretiv123456");const plaintext="This is my message";const ciphertext=CryptoJS.Blowfish.encrypt(plaintext,key,{iv:iv});console.log(ciphertext.toString());const decrypted=CryptoJS.Blowfish.decrypt(ciphertext.toString(),key,{iv:iv});console.log(decrypted.toString(CryptoJS.enc.Utf8));